      Bar bar = new Bar();
      bar.setAge(33);

      assertEquals("test", echoImplPort.echoString("test"));
      assertEquals("Mary & Paul", echoImplPort.echoString("Mary & Paul"));
      assertEquals(bar.getAge(), echoImplPort.echoBar(bar).getAge());

